interface GridItemProps {
  icon: JSX.Element | string
  heading: string
  title: string
}

const GridItem: FC<GridItemProps> = ({ icon, heading, title }) => {
  return (
    <div className="flex flex-col rounded-lg bg-[#121214] p-3 shadow-md hover:bg-[#121214]">
      <div className="mb-4 text-indigo-500">
        {typeof icon === 'string' ? (
          // Render image if icon is a string (URL)
          <Image
            src={icon}
            alt={heading}
            width={40}
            height={40}
            className="h-12 w-12 object-contain"
          />
        ) : (
          // Render JSX element if icon is not a string
          icon
        )}
      </div>
      <h3 className="text-xl font-semibold text-white">{heading}</h3>
      <p className="mt-2 text-sm text-gray-400">{title}</p>
    </div>
  )
}

